CREATE TABLE livros (_id INTEGER NOT NULL PRIMARY KEY, nome TEXT NOT NULL, autor TEXT NOT NULL, ano INTEGER, isbn TEXT, imagem TEXT, status INTEGER);
CREATE TABLE pessoas (_id INTEGER NOT NULL PRIMARY KEY, nome TEXT NOT NULL, telefone TEXT NOT NULL, email TEXT NOT NULL, foto TEXT);
CREATE TABLE emprestimo ( _id INTEGER NOT NULL PRIMARY KEY, pessoa_id INTEGER NOT NULL, livro_id INTEGER NOT NULL, data_emprestimo TEXT NOT NULL, data_devolucao TEXT NOT NULL);